mapper.select:
SELECT u.id AS userId, u."email" AS email, u."displayName" AS displayName, ip."categories" AS categories, iv."submittedAt" AS submittedAt FROM influencer_verifications iv JOIN users u ON u.id = iv."userId" LEFT JOIN influencer_profiles ip ON ip."userId" = u.id WHERE iv.status = 'PENDING'
AND iv."submittedAt" < #{cursor}
ORDER BY iv."submittedAt" DESC, iv.id DESC LIMIT #{limitPlusOne}

SELECT u.id AS userId, u."email" AS email, ac."companyName" AS companyName, ac."industry" AS industry, ac."businessRegistrationNumber" AS businessRegistrationNumber, av."submittedAt" AS submittedAt FROM advertiser_verifications av JOIN users u ON u.id = av."userId" LEFT JOIN advertiser_companies ac ON ac."userId" = u.id WHERE av.status = 'PENDING'
AND av."submittedAt" < #{cursor}
ORDER BY av."submittedAt" DESC, av.id DESC LIMIT #{limitPlusOne}
